void vision_colormap(struct vision_t * v, const char * type)
{
	if(v && (v->type == VISION_TYPE_RGB))
	{
		unsigned char * pr = &((unsigned char *)v->datas)[v->npixel * 0];
		unsigned char * pg = &((unsigned char *)v->datas)[v->npixel * 1];
		unsigned char * pb = &((unsigned char *)v->datas)[v->npixel * 2];
		const unsigned char (*cm)[3];
		switch(shash(type))
		{
		case 0x143c974a: /* "parula" */
			cm = colormap_parula;
			break;
		case 0x0b888348: /* "jet" */
			cm = colormap_jet;
			break;
		case 0x351d1e97: /* "rainbow" */
			cm = colormap_rainbow;
			break;
		default:
			cm = colormap_parula;
			break;
		}
		for(int i = 0; i < v->npixel; i++, pr++, pg++, pb++)
		{
			*pr = cm[*pr][0];
			*pg = cm[*pg][1];
			*pb = cm[*pb][2];
		}
	}
}
